import React, { useEffect, useState } from 'react';
import { Container, AppBar, Typography, Grow, Grid } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';

import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';

import { getPosts } from './actions/posts';

import useStyles from './styles';
import memories from './images/memories.png';

const App = () => {
    const [currentId, setCurrentId] = useState(null);
    const classes = useStyles();
    const dispatch = useDispatch();

    const posts = useSelector((state) => state.posts);
    const post = useSelector((state) => currentId ? state.posts.find((p) => p._id === currentId) : null);

    // add posts to dep array
    useEffect(() => {
        dispatch(getPosts());
    }, [currentId, dispatch, post]);



    //const posts = useSelector((state) => state.posts);

    return (
        <Container maxWidth="lg">
            <AppBar className={classes.appBar} position="static" color="inherit">
                <Typography className={classes.heading} variant="h2" align="center">
                    Memories
                </Typography>
                <img className={classes.image} src={memories} alt="" height="60" />
            </AppBar>
            <Grow in>
            <Container>
                <Grid container justifyContent="space-between" alignItems="stretch" spacing={3}>
                    <Grid item xs={12} sm={7}>
                        <Posts setCurrentId={setCurrentId} posts={posts}/>
                    </Grid>
                    <Grid item xs={12} sm={4}>
                        <Form currentId={currentId} setCurrentId={setCurrentId} post={post}/>
                    </Grid>
                </Grid>
            </Container>
            </Grow>

        </Container>

    )
}

export default App;
