import { AUTH } from '../constants/actionTypes';
import * as api from '../api/index.js';
//import { Navigate } from 'react-router-dom';


export const signin = (formData, router, setbadLogin) => async (dispatch) => {
  try {
    const { data } = await api.signIn(formData);

    dispatch({ type: AUTH, data });

    router('/'); //router.push('/'); is depreciated

  } catch (error) {
    console.log(error);
    setbadLogin(true);
  }
};

export const signup = (formData, router, setbadSignUp) => async (dispatch) => {
  console.log('signing up');
  try {
    console.log(formData.id);

    const { data } = await api.signUp(formData);

    console.log('signing up done');

    dispatch({ type: AUTH, data });

    // go back to posts homepage
    router('/'); //router.push('/'); is depreciated
  } catch (error) {
    console.log(error);
    setbadSignUp(true);
  //  return true;
  }
};
